mapper.update:
UPDATE article
title=#{title},
`body`=#{body}, updateDate = NOW() WHERE id = #{id}

mapper.select:
SELECT A.* ,M.nickname AS extra__writer FROM article AS A LEFT JOIN `member` AS M ON A.memberId = M.id WHERE 1
AND A.boardId = #{boardId}
ORDER BY id DESC
LIMIT #{limitStart},#{limitTake}
